dt-api.example:
$(document).ready(function() {
    var table = $('#example').DataTable({
        dom: 'Blfrtip',
        buttons:['createStateRestore', 'savedStates']
    });

    table.stateRestore.state("New State").delete()
});

$(document).ready(function() {
    var table = $('#example').DataTable({
        dom: 'Blfrtip',
        buttons:['createStateRestore', 'savedStates']
    });

    table.stateRestore.state("New State").delete(true)
});
