dt-option.example:
$(document).ready(function() {
    $('#example').DataTable( {
        language: {
            stateRestore: {
                creationModal:{
                    scroller: 'Scroller:'
                }
            }
        },
        buttons: [
            {
                extend: 'createState',
                config:{
                    creationModal: true,
                    toggle: {
                        scroller: true
                    }
                }
            }
            'savedStates'
        ],
        dom: 'Bfrtip'
    });
});
