dt-option.example:
$(document).ready(function() {
    $('#example').DataTable( {
        language: {
            stateRestore: {
                creationModal: {
                    columns: {
                        visible: 'Visible Columns:'
                    }
                }
            }
        },
        buttons: [
            {
                extend: 'createState',
                config:{
                    creationModal: true,
                    toggle: {
                        columns: {
                            visible: true
                        }
                    }
                }
            }
            'savedStates'
        ],
        dom: 'Bfrtip'
    });
});
