dt-example.js:
$('#example').DataTable({
	ajax: '../data/2500.txt',
	keys: true,
	layout: {
		top2Start: 'searchPanes',
		top1Start: 'searchBuilder',
		topStart: {
			buttons: [
				{
					extend: 'createState',
					config: {
						creationModal: true,
						toggle: {
							length: true,
							order: true,
							paging: true,
							scroller: true,
							search: true,
							searchBuilder: true,
							searchPanes: true,
							select: true
						}
					}
				},
				'savedStates'
			]
		}
	},
	rowId: 0,
	scrollY: 200,
	scrollCollapse: true,
	scroller: true,
	select: true
});

