dt-example.js:
$('#example').DataTable({
	language: {
		stateRestore: {
			removeSubmit: 'Confirm',
			removeConfirm: 'Confirm you want to remove %s.',
			emptyStates: 'No States',
			renameButton: 'Change Name',
			renameLabel: 'Rename to:'
		},
		buttons: {
			createState: 'New State',
			savedStates: 'Stored States',
			updateState: 'Store',
			stateRestore: 'New State %d',
			removeState: 'Remove',
			renameState: 'Change Name'
		}
	},
	layout: {
		topStart: {
			buttons: ['createState', 'savedStates']
		}
	}
});

