dt-example.js:
$(document).ready(function() {
    $('#example').DataTable({
        dom: 'Blfrtip',
        ajax:           "../data/2500.txt",
        deferRender:    true,
        scrollY:        200,
        scrollCollapse: true,
        scroller:       true,
        buttons:[
            'createState',
            {
                extend: 'savedStates',
                config: {
                    saveState: {
                        scroller: false
                    }
                },
            }
        ]
    });
});
