dt-example.js:
$(document).ready(function() {
    var table = $('#example').DataTable({
        dom: 'Blfrtip',
        buttons:['createState', 'savedStates'],
        language:{
            buttons:{
                savedStates: {
                    0: 'Saved States',
                    _: 'Saved States (%d)'
                }
            }
        }
    });

    table.button(1).text(table.i18n('buttons.savedStates', 'Saved States', table.stateRestore.states().length))

    table.on('stateRestore-change', function() {
        table.button(1).text(table.i18n('buttons.savedStates', 'Saved States', table.stateRestore.states().length))
    })
});
