dt-example.js:
$('#example').DataTable({
	ajax: {
		url: '../data/stateRestoreLoad.php',
		dataSrc: 'data'
	},
	layout: {
		topStart: {
			buttons: [
				'createState',
				{
					extend: 'savedStates',
					config: {
						ajax: '../data/stateRestoreSave.php'
					}
				}
			]
		}
	},
	scrollY: 200,
	scrollCollapse: true
});

dt-example.js-vanilla:
new DataTable('#example', {
	ajax: {
		url: '../data/stateRestoreLoad.php',
		dataSrc: 'data'
	},
	layout: {
		topStart: {
			buttons: [
				'createState',
				{
					extend: 'savedStates',
					config: {
						ajax: '../data/stateRestoreSave.php'
					}
				}
			]
		}
	},
	scrollY: 200,
	scrollCollapse: true
});
