$(document).ready(function() {
    $('#example').DataTable({
        dom: 'Blfrtip',
        buttons:[
            {
                extend: 'savedStates',
                config: {
                    splitSecondaries: [
                        'updateState',
                        'removeState',
                        '<h3>Custom HTML!</h3>',
                        'pdf',
                        {
                            extend: 'renameState',
                            className: 'red-border'
                        }
                    ]
                }
            },
            'createState'
        ]
    });
});
