dt-example.js:
$(document).ready(function() {
    $('#example').DataTable({
        dom: 'Blfrtip',
        ajax: {
          url: "../data/2500States.txt",
          dataSrc: 'data'
        },
        deferRender:    true,
        scrollY:        200,
        scrollCollapse: true,
        scroller:       true,
        buttons:['savedStates']
    });
});
