dt-example.js:
$(document).ready(function() {
    $('#example').DataTable({
        dom: 'PBlfrtip',
        buttons:[
            'createState',
            {
                extend: 'savedStates',
                config: {
                    preDefined: {
                        "London SP": {
                            searchPanes: {
                                selectionList: [
                                    {
                                        column: 2,
                                        rows: ['London']
                                    }
                                ]
                            }
                        }
                    }
                }
            }        
        ]
    });
});
