dt-example.js:
$(document).ready(function() {
    $('#example').DataTable({
        dom: 'PQBlfrtip',
        colReorder: true,
        ajax:           "../data/2500.txt",
        deferRender:    true,
        scrollY:        200,
        scrollCollapse: true,
        scroller:       true,
        buttons:[
            'createStateRestore',
            {
                extend: 'savedStates',
                config: {
                    creationModal: true,
                    toggle: {
                        colReorder: true,
                        columns:{
                            search: true,
                            visible: true
                        },
                        order: true,
                        paging: true,
                        scroller: true,
                        search: true,
                        searchBuilder: true,
                        searchPanes: true
                    }
                }
            }
        ]
    });
});
