dt-example.js:
$(document).ready(function() {
    var table = $('#example').DataTable({
        buttons:[
            {
                extend: 'createState',
                config: {
                    creationModal: true,
                    toggle: {
                        columns:{
                            search: true,
                            visible: true
                        },
                        len: true,
                        order: true,
                        paging: true,
                        search: true,
                    }
                }
            },
            'savedStates'
        ],
        lengthChange: false
    });

    table.buttons().container()
        .appendTo( '#example_wrapper .col-md-6:eq(0)' );
});
