dt-example.js:
$(document).ready(function() {
    var table = $('#example').DataTable({
        buttons:[
            'createStateRestore',
            {
                extend: 'savedStates',
                config: {
                    creationModal: true,
                    toggle: {
                        columns:{
                            search: true,
                            visible: true
                        },
                        order: true,
                        paging: true,
                        search: true,
                    }
                }
            }
        ],
        lengthChange: false
    });

    table.buttons().container()
        .appendTo( $('div.column.is-one-half', table.table().container()).eq(0) );
});
