dt-example.js:
$('#example').DataTable({
	layout: {
		topStart: {
			buttons: [
				{
					extend: 'createState',
					config: {
						creationModal: true,
						toggle: {
							columns: {
								search: true,
								visible: true
							},
							length: true,
							order: true,
							paging: true,
							search: true
						}
					}
				},
				'savedStates'
			]
		}
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	layout: {
		topStart: {
			buttons: [
				{
					extend: 'createState',
					config: {
						creationModal: true,
						toggle: {
							columns: {
								search: true,
								visible: true
							},
							length: true,
							order: true,
							paging: true,
							search: true
						}
					}
				},
				'savedStates'
			]
		}
	}
});
