dt-example.js:
$(document).ready(function() {
    let table = $('#example').DataTable({
        buttons:[
            {
                extend: 'createState',
                config: {
                    creationModal: true,
                    toggle: {
                        columns:{
                            search: true,
                            visible: true
                        },
                        len: true,
                        order: true,
                        paging: true,
                        search: true,
                    }
                }
            },
            'savedStates'
        ],
        lengthChange: false
    });

    table.buttons().container()
        .appendTo( $('div.eight.column:eq(0)', table.table().container()) );
});
