dt-example.js:
$(document).ready(function() {
    let table = $('#example').DataTable({
        buttons:[
            'createState',
            {
                extend: 'savedStates',
                config: {
                    creationModal: true,
                    toggle: {
                        columns:{
                            search: true,
                            visible: true
                        },
                        order: true,
                        paging: true,
                        search: true,
                    }
                }
            }
        ],
        lengthChange: false
    });

    table.buttons().container()
        .appendTo( $('div.eight.column:eq(0)', table.table().container()) );
});
